import React from 'react';
import '../index.css';
import Header from "./header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(!isEditProfilePopupOpen);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(!isAddPlacePopupOpen);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(!isEditAvatarPopupOpen);
  }

  function closeAllPopups(evt) {
    evt.target.closest('popup').classList.remove('popup_opened');
    //document.querySelectorAll('.popup').classList.remove('popup_opened');

  }

  return (
    <body className="page">
    <div className="content">
      <Header/>
      <Main
          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onEditAvatar={handleEditAvatarClick}
      />
      <Footer/>

      <PopupWithForm
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          name="profile"
          title="Редактировать профиль"
          button="Сохранить"
          children={(
              <>
                <input type="text" id="name-input" name="name" placeholder="Имя"
                       className="form__input form__input_name"
                       required minLength="2" maxLength="40" pattern="^[A-Za-zА-Яа-яЁё \-]+$"/>
                <span className="form__input-error" id="name-input-error"></span>
                <input type="text" id="description-input" name="about" placeholder="Описание"
                       className="form__input form__input_description" required minLength="2" maxLength="200"
                       pattern="^[A-Za-zА-Яа-яЁё, \-]+$"/>
                <span className="form__input-error" id="description-input-error"></span>
              </>
          )}
      />

      <PopupWithForm
          isOpen={isAddPlacePopupOpen}
          name="new-card"
          title="Новое место"
          button="Создать"
          children={(
              <>
                <input type="text" id="place-input" name="name" placeholder="Название"
                       className="form__input form__input_name" required minLength="2" maxLength="30"
                       pattern="^[A-Za-zА-Яа-яЁё \-]+$"/>
                <span className="form__input-error" id="place-input-error"></span>
                <input type="url" id="link-input" name="link" placeholder="Ссылка на картинку"
                       className="form__input form__input_link" required/>
                <span className="form__input-error" id="link-input-error"></span>
              </>
          )}
      />

      <PopupWithForm
        isOpen={isEditAvatarPopupOpen}
        name="avatar"
        title="Обновить аватар"
        button="Сохранить"
        children={(
            <>
              <input type="url" id="avatar-link-input" name="avatar" placeholder="Ссылка на картинку"
                     className="form__input form__input_link" required/>
              <span className="form__input-error" id="avatar-link-input-error"></span>
            </>
        )}
      />

      <section className="popup" id="popup__enlarged">
        <div className="popup__enlarged">
          <img className="popup__image"/>
            <p className="popup__place"></p>
            <button type="button" className="popup__close"></button>
        </div>
      </section>


      <section className="popup" id="popup__delete">
        <form className="form form_delete">
          <h3 className="form__title">Вы уверены?</h3>
          <button type="submit" name="Да" className="form__save">Да</button>
          <button type="button" className="popup__close"></button>
        </form>
      </section>

      <template id="card">
        <li className="element">
          <img className="element__image"/>
          <div className="element__сaption">
            <h3 className="element__title"></h3>
            <div className="element__likes">
              <button type="button" className="element__like"></button>
              <p className="element__like-counter">0</p>
            </div>
          </div>
          <button type="button" className="element__delete"></button>
        </li>
      </template>
    </div>
    </body>
  );
}

export default App;
