import React from "react";
import { Link } from "react-router-dom";
import Radium from 'radium';

function Login() {
  const linkStyle = {
    color: 'white',
    textDecoration: 'none',
    ':hover': {
      opacity: '0.6'
    }
  }

  return (
    <main>
      <section className="auth">

        <form className="auth-form">
          <h2 className="auth-form__title">Вход</h2>
          <input
            type="email"
            placeholder="Email"
            className="auth-form__input"
            required
          />
          <input
            type="password"
            placeholder="Пароль"
            className="auth-form__input"
            required
          />
          <button type="submit"
                  className="auth-form__submit"
          >
            Войти
          </button>
        </form>

        <p className="auth__redirect">
          Ещё не зарегистрированы? {<Link to="/sign-up" style={linkStyle}>Регистриация</Link>}
        </p>
      </section>
    </main>
  )
}

export default Radium(Login);
